import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import "./CircleAnimation.css";

const dots = 8;
const radius = 70; // size for both positions
const size = 220;

const getCirclePosition = (index, total, radius) => {
  const angle = (index / total) * 2 * Math.PI;
  return {
    left: size/2 + radius * Math.cos(angle),
    top: size/2 + radius * Math.sin(angle)
  };
};

const getDiamondPosition = (index, total, radius) => {
  // Define exact diamond points, 8 dots around center
  const diamond = [
    { x: 0, y: -radius }, 
    { x: radius/Math.SQRT2, y: -radius/Math.SQRT2 },
    { x: radius, y: 0 },
    { x: radius/Math.SQRT2, y: radius/Math.SQRT2 },
    { x: 0, y: radius },
    { x: -radius/Math.SQRT2, y: radius/Math.SQRT2 },
    { x: -radius, y: 0 },
    { x: -radius/Math.SQRT2, y: -radius/Math.SQRT2 },
  ];
  return {
    left: size/2 + diamond[index].x,
    top: size/2 + diamond[index].y
  };
};

export default function CircleDotsMotion() {
  const [toDiamond, setToDiamond] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setToDiamond((prev) => !prev);
    }, 3000); // toggles every 3 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="circle-dots-container" style={{marginTop:"80px", position:"relative"}}>
      {[...Array(dots)].map((_, idx) => {
        const circlePos = getCirclePosition(idx, dots, radius);
        const diamondPos = getDiamondPosition(idx, dots, radius);

        return (
          <motion.span
            key={idx}
            className="dot"
            animate={{
              left: toDiamond ? diamondPos.left : circlePos.left,
              top: toDiamond ? diamondPos.top : circlePos.top,
            }}
            transition={{ duration: 1.5, type: "spring" }}
            style={{
              position:"absolute",
              width:"12px",
              height:"12px",
              background:"#111",
              borderRadius:"50%",
              transform: "translate(-50%, -50%)"
            }}
          />
        );
      })}
      {/* Static center dot */}
      <span
        className="dot center-dot"
        style={{
          position:"absolute",
          width:"12px",
          height:"12px",
          background:"#111",
          borderRadius:"50%",
          left:size/2,
          top:size/2,
          transform: "translate(-50%, -50%)"
        }}
      />
    </div>
  );
}
