import React, { useState } from "react";
import { motion } from "framer-motion";
import "./CircleAnimation.css";

const dots = 8;
const radius = 70; // Use pixel units for explicit placement
const size = 220;

const getCirclePosition = (index, total, radius) => {
  const angle = (index / total) * 2 * Math.PI;
  return {
    left: size/2 + radius * Math.cos(angle),
    top: size/2 + radius * Math.sin(angle)
  };
};

const getDiamondPosition = (index, total, radius) => {
  // Arrange 4 on diagonals (N, E, S, W), 4 on axes between them
  const diamond = [
    { x: 0, y: -radius }, // top
    { x: radius/Math.SQRT2, y: -radius/Math.SQRT2 },
    { x: radius, y: 0 }, // right
    { x: radius/Math.SQRT2, y: radius/Math.SQRT2 },
    { x: 0, y: radius }, // bottom
    { x: -radius/Math.SQRT2, y: radius/Math.SQRT2 },
    { x: -radius, y: 0 }, // left
    { x: -radius/Math.SQRT2, y: -radius/Math.SQRT2 },
  ];
  return {
    left: size/2 + diamond[index].x,
    top: size/2 + diamond[index].y
  };
};

export default function CircleDotsMotion() {
  const [toDiamond, setToDiamond] = useState(false);

  return (
    <div className="circle-dots-container" style={{marginTop:"80px", position:"relative"}}>
      {[...Array(dots)].map((_, idx) => {
        const circ = getCirclePosition(idx, dots, radius);
        const diam = getDiamondPosition(idx, dots, radius);

        return (
          <motion.span
            key={idx}
            className="dot"
            animate={{
              left: toDiamond ? diam.left : circ.left,
              top: toDiamond ? diam.top : circ.top,
            }}
            transition={{ duration: 0.8, type: "spring" }}
            style={{
              position:"absolute",
              width:"12px",
              height:"12px",
              background:"#111",
              borderRadius:"50%",
              transform: "translate(-50%, -50%)"
            }}
          />
        );
      })}
      {/* Center dot */}
      <motion.span
        className="dot center-dot"
        style={{
          position:"absolute",
          width:"12px",
          height:"12px",
          background:"#111",
          borderRadius:"50%",
          left:size/2,
          top:size/2,
          transform: "translate(-50%, -50%)"
        }}
      />
      <button
        style={{ position: "absolute", bottom: -50, left: "50%", transform: "translateX(-50%)" }}
        onClick={() => setToDiamond((v) => !v)}
      >
        Toggle Animation
      </button>
    </div>
  );
}
