import React, { useState } from "react";
import { motion } from "framer-motion";
import "./CircleToDiamond.css";

export default function CircleToDiamond({ size = 200, color = "#000" }) {
  const [toDiamond, setToDiamond] = useState(false);
  const radius = Math.round(size * 0.35);

  // Circle positions (8 points around a circle)
  const circlePositions = Array.from({ length: 8 }, (_, i) => {
    const angle = (i * 45 * Math.PI) / 180;
    return {
      x: Math.cos(angle) * radius,
      y: Math.sin(angle) * radius,
    };
  });

  // Diamond positions (top, right, bottom, left, and diagonals)
  const diamondPositions = [
    { x: 0, y: -radius }, // top
    { x: radius, y: 0 },  // right
    { x: 0, y: radius },  // bottom
    { x: -radius, y: 0 }, // left
    { x: radius * 0.7, y: -radius * 0.7 }, // top-right diagonal
    { x: radius * 0.7, y: radius * 0.7 },  // bottom-right diagonal
    { x: -radius * 0.7, y: radius * 0.7 }, // bottom-left diagonal
    { x: -radius * 0.7, y: -radius * 0.7 }, // top-left diagonal
  ];

  return (
    <div className="container">
      <motion.div
        className="shape"
        style={{ width: size, height: size }}
        onClick={() => setToDiamond(!toDiamond)} // toggle on click
      >
        {/* Outer 8 dots */}
        {circlePositions.map((pos, i) => (
          <motion.div
            key={i}
            className="dot"
            initial={{ x: pos.x, y: pos.y }}
            animate={toDiamond ? diamondPositions[i] : pos}
            transition={{ duration: 1.2, ease: "easeInOut" }}
            style={{ background: color }}
          />
        ))}

        {/* Center dot */}
        <motion.div
          className="dot center-dot"
          animate={{ x: 0, y: 0 }}
          transition={{ duration: 1.2 }}
          style={{ background: color }}
        />
      </motion.div>
      <p className="hint">(Click the shape to toggle Circle â†” Diamond)</p>
    </div>
  );
}
