import React from "react";
import { motion } from "framer-motion";
import "./App.css";

export default function App({
  size = 300,
  dotSize = 14,
  centerSize = 12,
  duration = 6,
}) {
  const radius = Math.round(size * 0.36);

  // 8 positions around circle
  const circle = Array.from({ length: 8 }, (_, i) => {
    const angle = (i * 45 * Math.PI) / 180;
    return { x: Math.cos(angle) * radius, y: Math.sin(angle) * radius };
  });

  // diamond positions
  const diamond = [
    { x: 0, y: -radius, color: "red" }, // top
    { x: radius, y: 0, color: "blue" }, // right
    { x: 0, y: radius, color: "purple" }, // bottom
    { x: -radius, y: 0, color: "orange" }, // left
    { x: radius * 0.7, y: -radius * 0.7, color: "blue" }, // top-right
    { x: radius * 0.7, y: radius * 0.7, color: "purple" }, // bottom-right
    { x: -radius * 0.7, y: radius * 0.7, color: "orange" }, // bottom-left
    { x: -radius * 0.7, y: -radius * 0.7, color: "red" }, // top-left
  ];

  // keyframes for timing
  const times = [0, 0.33, 0.66, 1];

  return (
    <div className="app-container">
      <svg
        className="overlay-svg"
        width={size}
        height={size}
        viewBox={`${-size / 2} ${-size / 2} ${size} ${size}`}
      >
        {/* Lines from center â†’ each dot */}
        {diamond.map((d, i) => (
          <motion.line
            key={`center-${i}`}
            x1={0}
            y1={0}
            x2={d.x}
            y2={d.y}
            stroke={d.color}
            strokeWidth="2"
            strokeDasharray="0, 300"
            animate={{
              strokeDasharray: [
                "0, 300", // hidden
                "300, 300", // grow to full
                "300, 300", // hold
                "0, 300", // retract
              ],
            }}
            transition={{
              duration,
              times: [0.25, 0.33, 0.55, 0.66], // only active in diamond
              ease: "easeInOut",
              repeat: Infinity,
            }}
          />
        ))}

        {/* Lines connecting neighboring dots */}
        {diamond.map((d, i) => {
          const next = diamond[(i + 1) % diamond.length];
          return (
            <motion.line
              key={`edge-${i}`}
              x1={d.x}
              y1={d.y}
              x2={next.x}
              y2={next.y}
              stroke="black"
              strokeWidth="1.5"
              strokeDasharray="0, 400"
              animate={{
                strokeDasharray: [
                  "0, 400",
                  "400, 400",
                  "400, 400",
                  "0, 400",
                ],
              }}
              transition={{
                duration,
                times: [0.25, 0.33, 0.55, 0.66],
                ease: "easeInOut",
                repeat: Infinity,
              }}
            />
          );
        })}

        {/* Animated dots */}
        {circle.map((cPos, i) => (
          <motion.circle
            key={`dot-${i}`}
            r={dotSize / 2}
            fill={diamond[i].color}
            initial={{ cx: cPos.x, cy: cPos.y }}
            animate={{
              cx: [cPos.x, diamond[i].x, 0, cPos.x],
              cy: [cPos.y, diamond[i].y, 0, cPos.y],
            }}
            transition={{
              duration,
              ease: "easeInOut",
              repeat: Infinity,
              times,
            }}
          />
        ))}

        {/* Center dot */}
        <circle cx={0} cy={0} r={centerSize / 2} fill="black" />
      </svg>
    </div>
  );
}
