import React from "react";
import { motion } from "framer-motion";
import "./App.css";

export default function App({
  size = 320,
  dotSize = 14,
  centerSize = 12,
  duration = 12,
}) {
  const radius = Math.round(size * 0.36);

  // Circle positions
  const circle = Array.from({ length: 8 }, (_, i) => {
    const angle = (i * 45 * Math.PI) / 180;
    return { x: Math.cos(angle) * radius, y: Math.sin(angle) * radius };
  });

  // Diamond positions (clockwise)
  const diamond = [
    { x: 0, y: -radius, color: "red" },          // top
    { x: radius, y: 0, color: "blue" },          // right
    { x: 0, y: radius, color: "purple" },        // bottom
    { x: -radius, y: 0, color: "orange" },       // left
    { x: radius * 0.7, y: -radius * 0.7, color: "blue" },   // top-right
    { x: radius * 0.7, y: radius * 0.7, color: "purple" },  // bottom-right
    { x: -radius * 0.7, y: radius * 0.7, color: "orange" }, // bottom-left
    { x: -radius * 0.7, y: -radius * 0.7, color: "red" },   // top-left
  ];

  // Dot animation cycle: circle → diamond → center → circle
  const dotTimes = [0, 0.3, 0.9, 1];

  // Edge line cycle: start slightly after dots reach diamond
  const lineTimes = [0.35, 0.5, 0.7, 0.9];

  return (
    <div className="app-container">
      <svg
        className="overlay-svg"
        width={size}
        height={size}
        viewBox={`${-size / 2} ${-size / 2} ${size} ${size}`}
      >
        {/* Edge connections, clockwise, delayed */}
        {diamond.map((d, i) => {
          const next = diamond[(i + 1) % diamond.length];
          return (
            <motion.line
              key={`edge-${i}`}
              x1={d.x}
              y1={d.y}
              x2={next.x}
              y2={next.y}
              stroke="black"
              strokeWidth="1.5"
              strokeDasharray="0, 400"
              animate={{
                strokeDasharray: [
                  "0, 400",   // hidden
                  "400, 400", // draw
                  "400, 400", // hold
                  "0, 400",   // erase
                ],
              }}
              transition={{
                duration,
                times: lineTimes,
                ease: "easeInOut",
                repeat: Infinity,
                delay: i * 0.2, // sequential clockwise
              }}
            />
          );
        })}

        {/* Animated dots */}
        {circle.map((cPos, i) => (
          <motion.circle
            key={`dot-${i}`}
            r={dotSize / 2}
            fill={diamond[i].color}
            initial={{ cx: cPos.x, cy: cPos.y }}
            animate={{
              cx: [cPos.x, diamond[i].x, 0, cPos.x],
              cy: [cPos.y, diamond[i].y, 0, cPos.y],
            }}
            transition={{
              duration,
              ease: "easeInOut",
              repeat: Infinity,
              times: dotTimes,
            }}
          />
        ))}

        {/* Center dot */}
        <circle cx={0} cy={0} r={centerSize / 2} fill="black" />
      </svg>
    </div>
  );
}
