import React from "react";
import "./CircleAnimation.css";

/**
 * CircleAnimation
 * Props:
 *  - size: overall size in px (default 160)
 *  - color: dot color (default "#000")
 *  - speed: cycle duration in seconds (default 1.2)
 */
export default function CircleAnimation({ size = 160, color = "#000", speed = 1.2 }) {
  const radius = Math.round(size * 0.28); // distance of outer dots from center
  const styleObj = {
    width: `${size}px`,
    height: `${size}px`,
    // CSS variables used inside CSS file
    ["--dot-color"]: color,
    ["--radius"]: `${radius}px`,
    ["--speed"]: `${speed}s`,
  };

  return (
    <div className="circle-wrap">
      <div className="circle" style={styleObj}>
        {/* 8 outer positions */}
        {[...Array(8)].map((_, i) => (
          <div key={i} className={`outer pos${i + 1}`}>
            <div className="dot" />
          </div>
        ))}

        {/* center dot */}
        <div className="center">
          <div className="dot center-dot" />
        </div>
      </div>
    </div>
  );
}
