import React from "react";
import { motion } from "framer-motion";
import "../CircleToDiamondCollapse.css";
import ''
/**
 * CircleToDiamondCollapse
 * Props:
 *  - size: overall box size in px (default 220)
 *  - color: dot color
 *  - dotSize: outer dot size in px
 *  - centerSize: center dot size in px
 *  - duration: full cycle duration in seconds
 */
export default function CircleToDiamondCollapse({
  size = 220,
  color = "#000",
  dotSize = 14,
  centerSize = 12,
  duration = 5,
}) {
  const radius = Math.round(size * 0.36);

  // 8 circle positions around center (px)
  const circle = Array.from({ length: 8 }, (_, i) => {
    const angle = (i * 45 * Math.PI) / 180;
    return { x: Math.cos(angle) * radius, y: Math.sin(angle) * radius };
  });

  // diamond positions (px)
  const diamond = [
    { x: 0, y: -radius }, // top
    { x: radius, y: 0 }, // right
    { x: 0, y: radius }, // bottom
    { x: -radius, y: 0 }, // left
    { x: radius * 0.7, y: -radius * 0.7 }, // top-right
    { x: radius * 0.7, y: radius * 0.7 }, // bottom-right
    { x: -radius * 0.7, y: radius * 0.7 }, // bottom-left
    { x: -radius * 0.7, y: -radius * 0.7 }, // top-left
  ];

  // times for keyframes (0..1) — circle -> diamond -> center -> circle
  const times = [0, 0.33, 0.66, 1];

  return (
    <div className="cd-container">
      <motion.div
        className="cd-shape"
        style={{ width: size, height: size }}
        aria-hidden
      >
        {circle.map((cPos, i) => (
          <motion.div
            className="dot-holder"
            key={i}
            initial={{ x: cPos.x, y: cPos.y }}
            animate={{
              x: [cPos.x, diamond[i].x, 0, cPos.x],
              y: [cPos.y, diamond[i].y, 0, cPos.y],
            }}
            transition={{
              duration,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "loop",
              times,
            }}
          >
            <div
              className="dot"
              style={{
                width: `${dotSize}px`,
                height: `${dotSize}px`,
                background: color,
              }}
            />
          </motion.div>
        ))}

        {/* center dot always at center */}
        <div className="dot-holder center-holder" aria-hidden>
          <div
            className="dot center-dot"
            style={{
              width: `${centerSize}px`,
              height: `${centerSize}px`,
              background: color,
            }}
          />
        </div>
      </motion.div>
    </div>
  );
}
