import React from "react";
import { motion } from "framer-motion";
import "./CircleDiamondCollapse.css";

export default function CircleDiamondCollapse({ size = 200, color = "#000" }) {
  const radius = Math.round(size * 0.35);

  // Circle positions (8 points around a circle)
  const circlePositions = Array.from({ length: 8 }, (_, i) => {
    const angle = (i * 45 * Math.PI) / 180;
    return {
      x: Math.cos(angle) * radius,
      y: Math.sin(angle) * radius,
    };
  });

  // Diamond positions
  const diamondPositions = [
    { x: 0, y: -radius }, // top
    { x: radius, y: 0 },  // right
    { x: 0, y: radius },  // bottom
    { x: -radius, y: 0 }, // left
    { x: radius * 0.7, y: -radius * 0.7 }, // top-right
    { x: radius * 0.7, y: radius * 0.7 },  // bottom-right
    { x: -radius * 0.7, y: radius * 0.7 }, // bottom-left
    { x: -radius * 0.7, y: -radius * 0.7 }, // top-left
  ];

  return (
    <div className="container">
      <motion.div className="shape" style={{ width: size, height: size }}>
        {/* Outer 8 dots */}
        {circlePositions.map((pos, i) => (
          <motion.div
            key={i}
            className="dot"
            animate={{
              x: [pos.x, diamondPositions[i].x, 0, pos.x],
              y: [pos.y, diamondPositions[i].y, 0, pos.y],
            }}
            transition={{
              duration: 6,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "loop",
            }}
            style={{ background: color }}
          />
        ))}

        {/* Center dot (always in middle) */}
        <motion.div
          className="dot center-dot"
          style={{ background: color }}
        />
      </motion.div>
    </div>
  );
}
