import React from "react";
import { motion } from "framer-motion";
import "./App.css";

export default function App({
  size = 360,
  dotSize = 14,
  centerSize = 12,
  duration = 2,
}) {
  const radius = Math.round(size * 0.36);

  // Circle positions
  const circle = Array.from({ length: 8 }, (_, i) => {
    const angle = (i * 45 * Math.PI) / 180;
    return { x: Math.cos(angle) * radius, y: Math.sin(angle) * radius };
  });

  // Diamond positions (clockwise)
  const diamond = [
    { x: 0, y: -radius, color: "red" }, // top
    { x: radius, y: 0, color: "blue" }, // right
    { x: 0, y: radius, color: "purple" }, // bottom
    { x: -radius, y: 0, color: "orange" }, // left
    { x: radius * 0.7, y: -radius * 0.7, color: "blue" },
    { x: radius * 0.7, y: radius * 0.7, color: "purple" },
    { x: -radius * 0.7, y: radius * 0.7, color: "orange" },
    { x: -radius * 0.7, y: -radius * 0.7, color: "red" },
  ];

  // Edge connections
  const connections = [
    [0, 4], [4, 1], [1, 5], [5, 2],
    [2, 6], [6, 3], [3, 7], [7, 0],
    [4, 5], [5, 6], [6, 7], [7, 4],
  ];

  return (
    <div className="app-container">
      <svg
        className="overlay-svg"
        width={size}
        height={size}
        viewBox={`${-size / 2} ${-size / 2} ${size} ${size}`}
      >
        {/* Center-to-dots dotted lines */}
        {diamond.map((d, i) => (
          <motion.line
            key={`center-${i}`}
            x1={0}
            y1={0}
            x2={d.x}
            y2={d.y}
            stroke="gray"
            strokeWidth="1.5"
            strokeDasharray="4,4"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: [0, 1, 0] }}
            transition={{
              duration: duration * 4,
              ease: "easeInOut",
              repeat: Infinity,
              delay: i * 0.2, // Staggered
            }}
          />
        ))}

        {/* Edge dotted lines */}
        {connections.map(([s, e], idx) => {
          const start = diamond[s];
          const end = diamond[e];
          return (
            <motion.line
              key={`edge-${idx}`}
              x1={start.x}
              y1={start.y}
              x2={end.x}
              y2={end.y}
              stroke="limegreen"
              strokeWidth="2"
              strokeDasharray="6,6"
              initial={{ pathLength: 0 }}
              animate={{ pathLength: [0, 1, 0] }}
              transition={{
                duration: duration * 4,
                ease: "easeInOut",
                repeat: Infinity,
                delay: idx * 0.3, // Circular sequence
              }}
            />
          );
        })}

        {/* Moving dots */}
        {diamond.map((target, i) => {
          const c = circle[i];
          return (
            <motion.circle
              key={`dot-${i}`}
              r={dotSize / 2}
              fill={target.color}
              initial={{ cx: 0, cy: 0 }}
              animate={{
                cx: [0, c.x, target.x, c.x, 0],
                cy: [0, c.y, target.y, c.y, 0],
              }}
              transition={{
                duration: duration * 4,
                ease: "easeInOut",
                repeat: Infinity,
                delay: i * 0.2, // Sequential delay
              }}
            />
          );
        })}

        {/* Center dot */}
        <circle cx={0} cy={0} r={centerSize / 2} fill="black" />
      </svg>
    </div>
  );
}
