import React from "react";
import { motion } from "framer-motion";
import "./App.css";

export default function App({
  size = 300,
  dotSize = 14,
  centerSize = 12,
  duration = 6,
}) {
  const radius = Math.round(size * 0.36);

  // 8 positions around circle
  const circle = Array.from({ length: 8 }, (_, i) => {
    const angle = (i * 45 * Math.PI) / 180;
    return { x: Math.cos(angle) * radius, y: Math.sin(angle) * radius };
  });

  // diamond positions
  const diamond = [
    { x: 0, y: -radius, color: "red" }, // top
    { x: radius, y: 0, color: "blue" }, // right
    { x: 0, y: radius, color: "purple" }, // bottom
    { x: -radius, y: 0, color: "red" }, // left
    { x: radius * 0.7, y: -radius * 0.7, color: "blue" }, // top-right
    { x: radius * 0.7, y: radius * 0.7, color: "purple" }, // bottom-right
    { x: -radius * 0.7, y: radius * 0.7, color: "purple" }, // bottom-left
    { x: -radius * 0.7, y: -radius * 0.7, color: "red" }, // top-left
  ];

  // keyframes (0 = circle, 0.33 = diamond, 0.66 = center, 1 = circle again)
  const times = [0, 0.33, 0.66, 1];

  return (
    <div className="app-container">
      <svg
        className="overlay-svg"
        width={size}
        height={size}
        viewBox={`${-size / 2} ${-size / 2} ${size} ${size}`}
      >
        {/* Animated lines from center → diamond → back */}
        {diamond.map((d, i) => (
          <motion.line
            key={i}
            x1={0}
            y1={0}
            x2={0}
            y2={0}
            stroke={d.color}
            strokeWidth="2"
            strokeDasharray="5,5"
            animate={{
              x2: [0, d.x, 0],
              y2: [0, d.y, 0],
            }}
            transition={{
              duration,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "loop",
              times: [0.2, 0.33, 0.66], // only active in diamond phase
            }}
          />
        ))}

        {/* Animated dots */}
        {circle.map((cPos, i) => (
          <motion.circle
            key={i}
            r={dotSize / 2}
            fill={diamond[i].color}
            initial={{ cx: cPos.x, cy: cPos.y }}
            animate={{
              cx: [cPos.x, diamond[i].x, 0, cPos.x],
              cy: [cPos.y, diamond[i].y, 0, cPos.y],
            }}
            transition={{
              duration,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "loop",
              times,
            }}
          />
        ))}

        {/* Center dot */}
        <circle
          cx={0}
          cy={0}
          r={centerSize / 2}
          fill="black"
        />
      </svg>
    </div>
  );
}
